/* Wrap final summary unter final_summary */
const router = $json || {};
let text = '';
if (router.text) text = String(router.text);
else if (router.output) text = String(router.output);
else if (router.choices && router.choices[0] && router.choices[0].message && router.choices[0].message.content) text = String(router.choices[0].message.content);
return [{ json: { final_summary: text } }];
