// --- Dateiname -> Datum ---
const fn   = item.binary?.file?.fileName || item.json.filename || 'meeting.m4a';
const mIso = fn.match(/\d{4}-\d{2}-\d{2}/);
const mDe  = fn.match(/\b(\d{1,2})\.(\d{1,2})\.(\d{4})\b/);
const d    = mIso ? mIso[0]
  : (mDe ? `${mDe[3]}-${String(mDe[2]).padStart(2,'0')}-${String(mDe[1]).padStart(2,'0')}`
         : new Date().toISOString().slice(0,10));

// --- Helper ---
const clean = v => String(v ?? '').replace(/\r?\n--?$/,'').replace(/\r?\n/g,'').trim();
const toBool = v => {
  if (typeof v === 'boolean') return v;
  const s = clean(v).toLowerCase();
  return ['1','true','yes','on'].includes(s);
};

// --- Rohfelder s√§ubern ---
const title          = clean(item.json.title);
const speaker_hints  = clean(item.json.speaker_hints);
const meeting_id_in  = clean(item.json.meeting_id);
const language       = clean(item.json.language);
const tags           = clean(item.json.tags);
const api_key        = clean(item.json.api_key);

// Flags robust lesen (egal ob *_flag oder ohne _flag geschickt wurde)
const diarizeB    = toBool(item.json.diarize_flag   ?? item.json.diarize   ?? 'true');
const identifyB   = toBool(item.json.identify_flag  ?? item.json.identify  ?? 'true');
const summarizeB  = toBool(item.json.summarize_flag ?? item.json.summarize ?? 'true');

// --- Endg√ºltiges JSON (nur fertige Werte einsetzen) ---
item.json = {
  meeting_id:     (meeting_id_in || `${d}-meeting`).toLowerCase(),
  meeting_date:   d,
  title:          title || `Sitzung ${d}`,

  // echte Booleans f√ºr IF-Nodes
  diarize_flag:   diarizeB,
  identify_flag:  identifyB,
  summarize_flag: summarizeB,

  // optionale String-Spiegel f√ºr alte Nodes / Logging
  diarize:        diarizeB   ? 'true' : 'false',
  identify:       identifyB  ? 'true' : 'false',
  summarize:      summarizeB ? 'true' : 'false',

  // passthrough
  speaker_hints,
  language,
  tags,
  api_key,
};

return item;

// Purify Audio: extrahiere Datei UND Form-Felder aus einem multipart/form-data Body

// Quelle bestimmen
const src =
  (item.binary?.file?.mimeType === 'multipart/form-data') ? 'file' :
  (item.binary?.data?.mimeType === 'multipart/form-data') ? 'data' :
  null;

if (!src) {
  // Schon ‚Äûsauber‚Äú? Dann nur zur√ºckgeben.
  return item;
}

// rohen multipart-Body holen
const mp = await this.helpers.getBinaryDataBuffer(0, src);
const s  = mp.toString('binary');

// Boundary aus Header (falls vorhanden) oder aus Body
const ctHdr = (item.headers?.['content-type'] || item.json?.headers?.['content-type'] || '').toString();
let m = ctHdr.match(/boundary=([^\s;]+)/i) || s.match(/^--([^\r\n]+)/);
let boundary = m ? (m[1].startsWith('--') ? m[1].slice(2) : m[1]) : null;
if (!boundary) throw new Error('multipart boundary nicht gefunden');

const delim = `--${boundary}`;
const parts = s.split(delim).slice(1, -1); // ohne Pr√§ambel und Abschluss

const fields = {};
for (const p of parts) {
  const idx = p.indexOf('\r\n\r\n');
  if (idx < 0) continue;
  const header = p.slice(0, idx);
  let body    = p.slice(idx + 4);
  // Body endet i.d.R. mit "\r\n"
  if (body.endsWith('\r\n')) body = body.slice(0, -2);

  const name = (header.match(/name="([^"]+)"/i) || [,''])[1];
  const fn   = (header.match(/filename="([^"]+)"/i) || [,''])[1];
  const ctp  = (header.match(/Content-Type:\s*([^\r\n]+)/i) || [,'application/octet-stream'])[1].trim();

  if (fn) {
    // Datei-Part
    const fileBuf = Buffer.from(body, 'binary');
    item.binary = item.binary || {};
    item.binary.file = {
      data:     fileBuf.toString('base64'),
      fileName: fn,
      mimeType: ctp,
    };
  } else if (name) {
    // normales Feld
    fields[name] = body;
  }
}

// Form-Felder in json mergen
item.json = { ...(item.json || {}), ...fields };

// alten multipart-Blob entsorgen (nur falls es 'data' war)
if (src === 'data') delete item.binary.data;

return item;

// Build Output (nach Combine Data)
// $json enth√§lt Metadaten + die Transcribe-Response flach in einem Objekt

const meeting = {
  meeting_id:  $json.meeting_id,
  meeting_date:$json.meeting_date,
  title:       $json.title,
};

const text = String($json.text || '').trim();

const segsRaw = Array.isArray($json.segments) ? $json.segments : [];
const segs = segsRaw.map(s => ({
  start_ms: Number(s.start_ms || 0),
  end_ms:   Number(s.end_ms   || 0),
  text:     String(s.text || '').trim(),
  name:     s.name || s.speaker || ($json.identify?.name || null),
})).filter(s => s.text);

const speakers = [...new Set(segs.map(s => s.name).filter(Boolean))];

return [{
  json: {
    meeting,
    speaker_hints: $json.speaker_hints || '',
    flags: {
      diarize_flag: $json.diarize_flag,
      identify:     $json.identify_flag,
    },
    transcribe: {
      text,
      segments: segs,
      identify: $json.identify || null,
    },
    speaking_shares: $json.speaking_shares || $json.diarize?.speaking_shares || $json.speech_ratios || []
  }
}];

// Inputs:
//  - Input 0: Upload/Webhook (mit Binary)
//  - Additional Input "Merge Diarize": JSON mit segments

const src = $input.first();            // Binary-Quelle
const bin = src.binary || {};
const binKeys = Object.keys(bin);
if (binKeys.length === 0) {
  throw new Error('Kein Binary in Input 0 gefunden.');
}
const firstKey = binKeys[0];
const outBinary = { ...bin };
if (firstKey !== 'file') outBinary.file = bin[firstKey];  // Spiegeln auf "file"

// Segmente vom "Merge Diarize"
const dia = $item(0, 'Merge Diarize')?.json ?? {};
const diaSegs = Array.isArray(dia.segments) ? dia.segments : [];

// Normalisieren
const norm = (v) => Math.round(Number(v ?? 0));
const segments = (diaSegs.length ? diaSegs : ($json.segments ?? []))
  .map(s => ({
    start_ms: norm(s.start_ms ?? s.from ?? s.start),
    end_ms:   norm(s.end_ms   ?? s.to   ?? s.end),
  }))
  .filter(s => s.end_ms > s.start_ms);

// Hints robust bauen (Array + CSV)
const hintsRaw = $json.speaker_hints ?? $json.hints ?? '';
const hintsArr = String(hintsRaw)
  .split(',')
  .map(s => s.trim())
  .filter(Boolean);
const hintsCsv = hintsArr.join(',');

return [{
  json: {
    // F√ºr Server, der JSON im multipart erwartet:
    segments,                                    // echtes Array
    // F√ºr Server, der Strings erwartet:
    segments_json: JSON.stringify({ segments }), // Kompatibilit√§t
    hints: hintsArr,                             // Array
    hints_csv: hintsCsv,                         // String-Variante
    sim_threshold: Number($json.sim_threshold ?? 0.65),
    top_k: Number($json.top_k ?? 3),
    api_key: $json.api_key || 'change-me'
  },
  binary: outBinary
}];

// üß© Chunk Transcript (Tokens) ‚Äì Audio & Document kompatibel
// Ziel: Text aus Transkript oder Dokument in sinnvolle Chunks (Satzende + L√§nge) teilen

// 1Ô∏è‚É£ Textquelle robust bestimmen
const text =
  $json.transcribe?.text ||
  $json.transcribe?.transcript ||
  $json.text ||
  $json.full_text ||
  $json.transcript ||
  '';

const clean = String(text).replace(/\s+/g, ' ').trim();
if (!clean) {
  return [
    {
      json: {
        error: 'Kein Text zum Chunken vorhanden',
        meeting: $json.meeting || {},
        meta: $json.meta || {},
      },
    },
  ];
}

// 2Ô∏è‚É£ Chunks nach Satzende oder L√§nge (~1500 Zeichen)
const sentences = clean.split(/(?<=[.!?])\s+/);
const chunks = [];
let buffer = [];

for (const sentence of sentences) {
  const current = buffer.join(' ');
  if ((current.length + sentence.length) > 1500) {
    chunks.push(buffer.join(' ').trim());
    buffer = [sentence];
  } else {
    buffer.push(sentence);
  }
}
if (buffer.length) chunks.push(buffer.join(' ').trim());

// 3Ô∏è‚É£ Items ausgeben
return chunks.map((chunk, i) => ({
  json: {
    chunk_idx: i + 1,
    chunk_text: chunk,
    meeting: $json.meeting || {},
    meta: $json.meta || {},
  },
}));

// üß± Build Chunk Summary Prompt ‚Äì kompatibel mit neuen clean chunks

const hdr = [];
const total = $json.total_chunks ?? $json.metrics?.total_chunks ?? 0;
const idx = $json.chunk_idx ?? $json.chunk?.idx ?? 0;

hdr.push(`üìÑ Chunk ${idx} / ${total}`);
hdr.push(`üóÇÔ∏è Quelle: ${$json.meta?.filename ?? 'unbekannt'}`);
hdr.push(`üìÖ Datum: ${$json.meeting?.meeting_date ?? 'n/a'}`);

if ($json.speaker_hints) hdr.push(`üîä Speaker-Hinweise: ${$json.speaker_hints}`);

const text = $json.chunk_text ?? $json.chunk?.text ?? $json.text ?? $json.transcript ?? $json.full_text ?? '[kein Text]';
const cleanText = text.trim();

return {
  json: {
    ...$json,
    rag: false,
    prompt: [
      hdr.join("\n"),
      '',
      cleanText,
      '',
      '‚úçÔ∏è Erstelle eine inhaltliche Zusammenfassung (fokus: Kernaussagen, Themen, Kontext).',
      'Formuliere neutral und pr√§gnant, keine Einleitung oder Floskeln.',
    ].filter(Boolean).join('\n'),
    system: 'Du bist ein strikter JSON-Generator. Keine Zusatztexte.',
    model: 'vllm',
    vllm_model: $env.VLLM_MODEL_ALLROUNDER ?? 'Owen/owen2.5-78b-instruct-GPTQ-int4',
    temperature: 0.2,
    top_p: 0.9,
    max_tokens: 800
  }
};

// üß© Normalize Chunk Result ‚Äì keep summary + transcript + segments + speaking_shares

function toJson(s) {
  try {
    return JSON.parse(String(s || '').replace(/^```json/i, '').replace(/```$/, '').trim());
  } catch {
    return null;
  }
}

const ans = $json.answer || $json.text || '';
const parsed = toJson(ans);
let summary = parsed || $json.summary || { summary: String(ans || '').slice(0, 800) };

// --- R√ºckw√§rtskompatibel / Daten √ºbernehmen ---
let transcript = '';
let segments = [];
let speaking_shares = [];

// 1Ô∏è‚É£ Daten aus Build Chunk Summary Prompt
try {
  const src = $items('Build Chunk Summary Prompt', 0)?.[0]?.json || {};
  transcript = src.transcript || src.full_text || '';
  segments = src.segments || [];
  speaking_shares = src.speaking_shares || [];
} catch {}

// 2Ô∏è‚É£ Fallback auf globale Daten (Build Output)
if ((!segments.length && !speaking_shares.length) || !transcript) {
  try {
    const global = $items('Build Output', 0)?.[0]?.json || {};
    transcript = transcript || global.transcribe?.text || global.text || '';
    segments = segments.length ? segments : (global.transcribe?.segments || global.segments || []);
    speaking_shares = speaking_shares.length ? speaking_shares : (global.speaking_shares || []);
  } catch {}
}

// 3Ô∏è‚É£ Letzter Fallback auf Chunk Transcript (Tokens)
if ((!segments.length && !speaking_shares.length) || !transcript) {
  try {
    const ct = $items('Chunk Transcript (Tokens)', 0)?.[0]?.json || {};
    transcript = transcript || ct.transcribe?.text || ct.text || '';
    segments = segments.length ? segments : (ct.segments || []);
    speaking_shares = speaking_shares.length ? speaking_shares : (ct.speaking_shares || []);
  } catch {}
}

// 4Ô∏è‚É£ Normalize arrays
segments = Array.isArray(segments) ? segments : [];
speaking_shares = Array.isArray(speaking_shares) ? speaking_shares : [];

// 5Ô∏è‚É£ R√ºckgabe ‚Äì summary NACH Spread, damit sie nicht √ºberschrieben wird
return [
  {
    json: {
      ...$json,
      transcript,
      segments,
      speaking_shares,
      summary
    }
  }
];

// üß© Build Reduce Prompt ‚Äì full version (prompt + system + model + meta)

const chunkSummaries = $json.chunk_summaries || [];
const combined = chunkSummaries.join('\n\n');

// Vorherige Summary (optional)
let previousSummary = '';
try {
  const prev = $items('Normalize Chunk Result', 0)?.[0]?.json?.summary;
  if (typeof prev === 'object' && prev.summary) previousSummary = prev.summary;
  else if (typeof prev === 'string') previousSummary = prev;
} catch {}

const meeting = $json.meeting || {};
const transcript = $json.transcript || '';
const segments = $json.segments || [];
const speaking_shares = $json.speaking_shares || [];

// --- System Prompt / Model ---
const system = `
Du bist ein analytischer Assistent. 
Fasse mehrere Teilzusammenfassungen zu einer konsistenten Gesamt√ºbersicht zusammen.
Gib das Ergebnis immer als g√ºltiges JSON zur√ºck:
{
  "summary": "string",
  "focus": ["string"],
  "questions": ["string"]
}
`;

const model = $json.model || 'vllm';
const rag = false;

// --- Finale Prompt ---
const prompt = `
### Einzelzusammenfassungen:
${combined}

### Vorherige Zusammenfassung:
${previousSummary || '[keine vorherige Summary]'}

Erstelle eine konsolidierte JSON-Zusammenfassung mit den Feldern "summary", "focus" und "questions".
`;

// --- R√ºckgabe f√ºr RAG Router ---
return [
  {
    json: {
      prompt,        // Text f√ºr das LLM
      system,        // Systemverhalten
      model,         // Modellwahl
      rag,           // keine Retrieval-Abfrage
      meeting,
      transcript,
      segments,
      speaking_shares
    }
  }
];

// üß© Finalize Summary ‚Äì include full document text for frontend rendering

function tryJson(s) {
  try {
    return JSON.parse(
      String(s || '').replace(/^```json/i, '').replace(/```$/, '').trim()
    );
  } catch {
    return null;
  }
}

// --- Parse summary ---
const ans = $json.answer || $json.text || '';
const parsed = tryJson(ans);
let summary = parsed || $json.summary || {};
if (!summary.summary && !summary.focus && !summary.questions) {
  summary = {
    summary: String(ans || '').slice(0, 800),
    focus: [],
    questions: []
  };
}

// --- Metadata / context ---
const meeting = $json.meeting || {};
const transcribe = $json.transcribe || {};
const segments = transcribe.segments || $json.segments || [];
const speaking_shares =
  $json.speaking_shares || transcribe.speaking_shares || [];

// --- Detect full text ---
let transcript =
  transcribe.text ||
  transcribe.transcript ||
  $json.transcript ||
  $json.full_text ||
  $json.text ||            // üëà Dokument-Text hier auffangen
  '';

// --- Flatten summary for frontend ---
const flat = {
  summary: summary.summary ?? summary.tldr ?? '',
  focus: summary.focus ?? [],
  questions: summary.questions ?? []
};

// --- Determine source ---
const source = meeting.meeting_id ? 'audio' : 'document';

// --- Unified output ---
return [
  {
    json: {
      meeting,
      ...flat,
      transcript,
      segments,
      speaking_shares,
      source
    }
  }
];

// üß© Normalize Reduce Result ‚Äì fixed for documents + audio

function toJson(s) {
  try {
    return JSON.parse(String(s || '').replace(/^```json/i, '').replace(/```$/, '').trim());
  } catch {
    return null;
  }
}

// 1Ô∏è‚É£ parse LLM answer
const ans = $json.answer || $json.text || '';
let parsed = toJson(ans);
let summary = parsed || $json.summary || {
  summary: String(ans || '').slice(0, 800),
  focus: [],
  questions: []
};

// 2Ô∏è‚É£ if summary.summary itself is a JSON string, parse again
if (typeof summary.summary === 'string' && summary.summary.trim().startsWith('{')) {
  const nested = toJson(summary.summary);
  if (nested && typeof nested === 'object') summary = nested;
}

// 3Ô∏è‚É£ collect metadata and transcript
let meeting = $json.meeting || {};
let transcribe = $json.transcribe || {};
let segments = $json.segments || [];
let speaking_shares = $json.speaking_shares || [];

// robust transcript extraction
let transcript = '';
try {
  // zuerst Build Reduce Prompt abfragen (da steckt full_text noch drin)
  const reduce = $items('Build Reduce Prompt', 0)?.[0]?.json || {};
  transcript = reduce.transcript || reduce.full_text || '';
} catch (e) {}

// falls nichts kam, noch Standard-Fallback
transcript = transcript ||
  transcribe.text ||
  transcribe.transcript ||
  $json.transcript ||
  $json.full_text ||
  $json.text ||
  '';

// create dummy meeting info for documents
if (!meeting.meeting_id) {
  meeting = {
    meeting_id: `document-${new Date().toISOString().slice(0,10)}`,
    meeting_date: new Date().toISOString().slice(0,10)
  };
}

// determine source
const isAudio = Boolean(transcribe.text || transcribe.transcript);
const source = isAudio ? 'audio' : 'document';

segments = $json.segments || $items('Normalize Chunk Result', 0)?.[0]?.json?.segments || [];
speaking_shares = $json.speaking_shares || $items('Normalize Chunk Result', 0)?.[0]?.json?.speaking_shares || [];

// 4Ô∏è‚É£ final output
return [
  {
    json: {
      meeting,
      summary,
      transcript,
      segments,
      speaking_shares,
      source
    }
  }
];

const segs = $json.transcribe?.segments || $json.segments || [];
const totals = {};
for (const s of segs) {
  const dur = Math.max(0, (s.end_ms ?? 0) - (s.start_ms ?? 0));
  const name = s.name || s.speaker || 'Unbekannt';
  totals[name] = (totals[name] || 0) + dur;
}
const sum = Object.values(totals).reduce((a,b)=>a+b,0) || 1;
const speaking_shares = Object.entries(totals).map(([name, ms]) => ({
  name,
  sekunden: Math.round(ms/1000),
  anteil_prozent: +(ms/sum*100).toFixed(1)
}));
return [{ json: { ...$json, speaking_shares } }];

// Detect File Type (n8n Code-Node) ‚Äî Run Once per Item

const file = item.binary?.file || {};
const mime = (file.mimeType || '').toLowerCase();
const name = (file.fileName || '').toLowerCase();

const audio = mime.startsWith('audio/') || /\.(m4a|mp3|wav|ogg|flac)$/i.test(name);
const document = !audio && (
  mime.startsWith('application/pdf') ||
  mime.startsWith('text/') ||
  /\.(pdf|docx?|odt|xlsx?|csv|txt|pptx?|eml|html?)$/i.test(name)
);

item.json.is_audio = audio;
item.json.is_document = document;
item.json.file_ext = name.split('.').pop() || '';

return item;

const now = new Date().toISOString().slice(0, 10);
const fn = item.binary?.file?.fileName || `document_${now}`;
const base = fn.replace(/\.[^.]+$/, '');

item.json.meeting = item.json.meeting || {
  meeting_id: base.toLowerCase(),
  meeting_date: now,
  title: item.json.title || base,
};

item.json.speaking_shares = item.json.speaking_shares || [];
item.json.transcribe = { text: item.json.text || '' };

// gleiche Flags wie bei Audio
item.json.summarize_flag = Boolean(item.json.summarize_flag ?? item.json.summarize ?? false);
item.json.tags = item.json.tags || 'document';

return item;

// üß© Merge Chunks (Full Text) ‚Äì intelligent handling for documents vs audio

const items = $input.all();
if (!items.length) return [];

//
// 1Ô∏è‚É£ Pr√ºfen, ob es sich um Audio-Chunks handelt (Segmentdaten vorhanden)
//
const looksLikeAudio = items.some(
  i =>
    i.json?.segment_start !== undefined ||
    i.json?.segment_end !== undefined ||
    (i.json?.chunk_text && /speaker|segment|utterance/i.test(JSON.stringify(i.json)))
);

//
// 2Ô∏è‚É£ AUDIO ‚Üí Original-Chunks unver√§ndert durchreichen
//
if (looksLikeAudio) {
  // √úbergib einfach alle Items weiter
  return items.map(i => ({
    json: {
      ...i.json,
      source: 'audio'
    }
  }));
}

//
// 3Ô∏è‚É£ DOKUMENT ‚Üí Volltext zusammenf√ºhren
//
const chunks = items
  .map(i => i.json.chunk_text || i.json.text || '')
  .filter(Boolean);

const fullText = chunks.join('\n\n').trim();

const meta = items[0]?.json?.meta || {};
const meeting = items[0]?.json?.meeting || {};

return [
  {
    json: {
      source: 'document',
      meeting,
      meta,
      transcript: fullText,
      full_text: fullText,
      chunks_total: chunks.length
    }
  }
];
