// Prepare Persona Items ‚Äî persona-only board, focus/questions in Prompt, robust provider/model mapping

// --------------- helpers ---------------
const norm = v => String(v ?? '').trim();
const isArr = a => Array.isArray(a);

// zugelassene Provider f√ºr den Router
const ALLOWED = new Set(['groq','openai','anthropic','mistralapi','openrouter','huggingface','mistral']);

// Heuristik: aus einem Modellstring den Provider raten
function guessProviderFromModel(m) {
  const s = norm(m).toLowerCase();
  if (!s) return '';
  if (s.includes('claude')) return 'anthropic';
  if (s.includes('gpt') || s.startsWith('o3') || s.startsWith('o1')) return 'openai';
  if (s.includes('openai/') || s.includes('/')) return 'openrouter';
  if (s.includes('mistral') || s.includes('mixtral')) return 'mistral';
  if (s.includes('llama') || s.includes('gemma') || s.includes('deepseek')) return 'groq';
  return '';
}

// Provider/Model zu Router-Feldern mappen (+ Defaults)
function mapProviderModel(providerIn, modelIn) {
  let prov = norm(providerIn).toLowerCase();
  let mdl  = norm(modelIn);

  // wenn prov kein erlaubter Provider ist, k√∂nnte es das Modell sein
  if (!ALLOWED.has(prov)) {
    const guessed = guessProviderFromModel(prov);
    if (guessed) { mdl = prov; prov = guessed; }
  }
  // weiterhin unklar? aus mdl raten
  if (!ALLOWED.has(prov)) {
    const guessed = guessProviderFromModel(mdl);
    if (guessed) prov = guessed;
  }
  // finale Defaults
  if (!ALLOWED.has(prov)) prov = 'groq';
  if (!mdl) {
    if (prov === 'groq')        mdl = 'llama-3.1-70b-versatile';
    else if (prov === 'openai') mdl = 'gpt-4o-mini';
    else if (prov === 'anthropic') mdl = 'claude-3-5-sonnet';
    else if (prov === 'mistralapi') mdl = 'mistral-large-latest';
    else if (prov === 'openrouter') mdl = 'openai/gpt-oss-120b';
    else if (prov === 'huggingface') mdl = 'meta-llama/Meta-Llama-3-8B-Instruct';
    else /* mistral (ollama) */ mdl = 'llama3';
  }

  switch (prov) {
    case 'groq':        return { model: 'groq',        groq_model: mdl };
    case 'openai':      return { model: 'openai',      openai_model: mdl };
    case 'anthropic':   return { model: 'anthropic',   claude_model: mdl };
    case 'mistralapi':  return { model: 'mistralapi',  mistral_model: mdl };
    case 'openrouter':  return { model: 'openrouter',  openrouter_model: mdl };
    case 'huggingface': return { model: 'huggingface', hf_model: mdl };
    case 'mistral':
    default:            return { model: 'mistral',     ollama_model: mdl };
  }
}

// --------------- input normalize ---------------
const root  = ($json && typeof $json === 'object') ? $json : {};
let pre = root;

// falls ein Preflight-Node die Personas normalisiert hat, dort abholen
if (!root.personas && !root.body?.personas) {
  try {
    const it = $items("Agents Preflight (Normalize)", 0)[0];
    if (it && it.json) pre = it.json;
  } catch {}
}

// Personas extrahieren
const personas =
  isArr(root.personas) ? root.personas :
  isArr(pre.personas)  ? pre.personas  :
  isArr(root.body?.personas) ? root.body.personas :
  [];

// keine Personas ‚Üí erkl√§rbares Fehlerobjekt
if (!isArr(personas) || personas.length === 0) {
  return [{ json: { error: "No personas provided", info: "Prepare Persona Items erwartet personas[]" } }];
}

// State & globale Felder
const state     = root.state || {};
const task      = norm(root.prompt ?? pre.prompt ?? root.body?.prompt ?? state.task);
const globalSys = norm(root.system ?? pre.system ?? root.body?.system);
const rag       = !!(root.rag ?? pre.rag ?? root.body?.rag);
const rag_tags  = isArr(root.rag_tags ?? pre.rag_tags ?? root.body?.rag_tags) ? (root.rag_tags ?? pre.rag_tags ?? root.body?.rag_tags) : [];
const convId    = root.conversation_id ?? pre.conversation_id ?? null;
const jobId     = root.job_id ?? pre.job_id ?? root.body?.job_id ?? root.jobId ?? pre.jobId ?? null;
const memory    = root.memory ?? pre.memory ?? null;

const round         = Number(state.round ?? root.round ?? 1);
const agent_rounds  = Number(state.rounds_total ?? state.agent_rounds ?? root.agent_rounds ?? pre.agent_rounds ?? 1);

// Persona-only Board als Kontext
const boardPersonaText = norm(state.boardPersonaText || root.boardPersonaText || root.boardText);

// Critic-Hinweise nur im Prompt anzeigen (nicht ins Board)
const focus = Array.isArray(state.critic_focus) ? state.critic_focus : [];
const questions = Array.isArray(state.critic_questions) ? state.critic_questions : [];

const focusLines = (focus.length ? [`- Beantworte diese Fokusaufgaben:`, ...focus.map(s => `  - ${norm(s)}`)] : []);
const questionLines = (questions.length ? [`- Beantworte diese Fragen:`, ...questions.map(s => `  - ${norm(s)}`)] : []);

// --------------- build per-persona items ---------------
const items = personas.map((p, idx) => {
  const label = norm(p.label || `Persona ${idx+1}`);
  const mapped = mapProviderModel(p.provider, p.model);

  const roleSystem = [globalSys, `Rolle: ${label}.`].filter(Boolean).join('\n\n');

  const personaPrompt = [
    `Teamaufgabe: ${task}`,
    `Dein Beitrag (Rolle: "${label}") in Runde ${round} von ${agent_rounds}:`,
    `- Liefere einen fokussierten, in sich verst√§ndlichen Abschnitt (max. 12 S√§tze).`,
    `- Beziehe dich auf die bisherigen Team-Ergebnisse (nur Persona-Inhalte).`,
    ...focusLines,
    ...questionLines,
    boardPersonaText ? `\nBisherige Team-Ergebnisse (Persona-Inhalte):\n${boardPersonaText}` : ''
  ].filter(Boolean).join('\n');

  return {
    json: {
      ...mapped,                  // <- setzt model + provider-spezifisches Modellfeld
      label,
      prompt: personaPrompt,
      system: roleSystem,
      rag, rag_tags,
      conversation_id: convId,
      memory,
      round,
      agent_rounds,
      // r√ºckw√§rts-kompatible Felder:
      board: Array.isArray(root.board) ? root.board : [],
      boardText: boardPersonaText,
      job_id: jobId
    }
  };
});

return items;

// Collect Agent Outputs ‚Äî persona-only, strip <think>, collect thinkPersona[]
// Quelle f√ºr Rundeninfos aus "Prepare Persona Items"
const preItems  = $items('Prepare Persona Items', 0);
const baseState = preItems?.[0]?.json ?? {};
const prevState = $json || {};
const state     = { ...baseState, ...prevState };

const agentResults = $input.all();

// Collect Agent Outputs ‚Äî persona-only, robust think-extraction
function extractThinkAndClean(s) {
  const src = String(s || '');
  const thinks = [];

  // 1) <think>...</think>
  let clean = src.replace(/<think>([\s\S]*?)<\/think>\s*/gi, (_, t) => {
    const seg = String(t || '').trim(); if (seg) thinks.push(seg); return '';
  });

  // 2) ```think ... ```
  clean = clean.replace(/```(?:think|thinking|reasoning)[\t ]*\n([\s\S]*?)```/gi, (_, t) => {
    const seg = String(t || '').trim(); if (seg) thinks.push(seg); return '';
  });

  // 3) Markdown-Heading "### Think ... (bis zum n√§chsten Heading oder Ende)"
  clean = clean.replace(/(^|\n)#{2,3}\s*think\b[\s\S]*?(?=(\n#{2,3}\s)|$)/gi, (m) => {
    const seg = m.replace(/^.*?\n/, '').trim(); if (seg) thinks.push(seg); return '';
  });

  // 4) "THINK:" Block (bis zur n√§chsten Leerzeile / Heading / Ende)
  clean = clean.replace(/(^|\n)think:\s*([\s\S]*?)(?=\n\s*\n|\n#{1,6}\s|$)/gi, (_, __, t) => {
    const seg = String(t || '').trim(); if (seg) thinks.push(seg); return '';
  });

  return { clean: clean.trim(), thinks };
}

const board = agentResults.map(it => {
  const j   = it.json || {};
  const raw = (j.text || j.result || '').trim();
  if (!raw) return null;
  const { clean, thinks } = extractThinkAndClean(raw);
  const label    = j.label || j.persona?.label || 'Agent';
  const provider = j.provider || j.model || j.model_used || '';
  return { label, text: clean, provider, thinks };
}).filter(Boolean);

const boardPersonaText = board.map(b => `### ${b.label}\n${b.text}`).join('\n\n');

const thinkPersona = Array.isArray(state.thinkPersona) ? state.thinkPersona.slice() : [];
board.forEach(b => {
  if (b.thinks && b.thinks.length) {
    thinkPersona.push({
      round: Number(state.round || 1),
      label: b.label,
      raw: b.thinks
    });
  }
});

return [{
  json: {
    ...state,
    board: board.map(({ thinks, ...rest }) => rest),
    boardText: boardPersonaText,     // r√ºckw√§rts-kompatibel
    boardPersonaText,                // Writer-Material
    thinkPersona
  }
}];

// Format Agent Response ‚Äî writer-only final text, JSON/Focus/Questions herausziehen, think-strip, Moderator-Notes anh√§ngen

function pickStr(...cands){ for (const v of cands) if (typeof v === 'string' && v.trim()) return v.trim(); return ''; }
function get(o, p){ return p.split('.').reduce((a,k)=>(a && a[k] !== undefined ? a[k] : undefined), o); }
function stripThink(s){ return String(s || '').replace(/<think>[\s\S]*?<\/think>\s*/gi, '').trim(); }

// --- NEW: JSON/Sections erkennen -> in Notes verschieben
function formatNotes(obj){
  const parts = [];
  if (obj.summary) parts.push(`### Zusammenfassung (Moderator)\n${String(obj.summary).trim()}`);
  if (Array.isArray(obj.focus) && obj.focus.length) parts.push(`### Fokus f√ºr die n√§chste Runde\n${obj.focus.map(s=>`- ${String(s).trim()}`).join('\n')}`);
  if (Array.isArray(obj.questions) && obj.questions.length) parts.push(`### Fragen f√ºr die n√§chste Runde\n${obj.questions.map(s=>`- ${String(s).trim()}`).join('\n')}`);
  return parts.join('\n\n');
}
function tryParseJson(s){
  try { return JSON.parse(s); } catch { return null; }
}
function extractEmbeddedModeratorFromText(txt){
  let out = String(txt || '');
  let collected = [];

  // 1) ```json ... ```
  out = out.replace(/```json\s*([\s\S]*?)```/gi, (_, body)=>{
    const parsed = tryParseJson(String(body||'').trim());
    if (parsed && (parsed.summary || parsed.focus || parsed.questions)) {
      const note = formatNotes(parsed);
      if (note) collected.push(note);
      return '';
    }
    return '';
  });

  // 2) reines JSON-Objekt als ganzer Text
  const trimmed = out.trim();
  if (trimmed.startsWith('{') && trimmed.endsWith('}')) {
    const parsed = tryParseJson(trimmed);
    if (parsed && (parsed.summary || parsed.focus || parsed.questions)) {
      const note = formatNotes(parsed);
      if (note) collected.push(note);
      out = '';
    }
  }

  // 3) √úberschriften/Abschnitte "Fokus/Fragen" im Text (LLM-Eigenleistung) entfernen ‚Üí Notes sammeln
  function harvestSection(name){
    // Bis n√§chste √úberschrift (### ...) oder Ende
    const re = new RegExp(`(^|\\n)###\\s*${name}\\b[\\s\\S]*?(?=(\\n###\\s)|$)`, 'gi');
    out = out.replace(re, (m)=>{
      const body = m.replace(/^.*?\n/, '').trim();
      if (body) {
        const lines = body.split(/\r?\n/).map(s=>s.replace(/^\s*[-*]\s?/, '').trim()).filter(Boolean);
        const obj = (name.toLowerCase().startsWith('fokus')||name.toLowerCase().startsWith('focus'))
          ? { focus: lines } : { questions: lines };
        const note = formatNotes(obj);
        if (note) collected.push(note);
      }
      return '';
    });
  }
  harvestSection('Fokus');
  harvestSection('Focus');
  harvestSection('Fragen');
  harvestSection('Questions');

  return { text: out.trim(), notes: collected.filter(Boolean).join('\n\n') };
}
function unwrapWholeTextBrackets(s){
  // Entfernt nur EIN Paar rund/ eckig, wenn es den GESAMTEN Text umschlie√üt.
  const t = String(s||'').trim();
  const m1 = t.match(/^\(\s*([\s\S]*?)\s*\)$/);
  if (m1) return m1[1].trim();
  const m2 = t.match(/^\[\s*([\s\S]*?)\s*\]$/);
  if (m2) return m2[1].trim();
  return t;
}
function sanitizeAnswer(s){
  let out = stripThink(s);
  // JSON & Sections herausziehen (hier nur der Text; Notes sammeln wir separat)
  const ex = extractEmbeddedModeratorFromText(out);
  out = ex.text;
  // f√ºhrende/backende Klammern, wenn sie *alles* umschlie√üen
  out = unwrapWholeTextBrackets(out);
  // √ºbrig gebliebene ```json```/```...``` Fences ohne Inhalt entsorgen
  out = out.replace(/```[\s\S]*?```/g, '').trim();
  return { text: out, embeddedNotes: ex.notes };
}

// --------- Bestehende Flatten-Logik (gek√ºrzt aus deiner Version) ---------
function tryParse(x){ if (typeof x === 'string') { try { return JSON.parse(x); } catch {} } return x; }
function flattenToPayload(data){
  let cur = data, hops = 0;
  while (cur && typeof cur === 'object' && !Array.isArray(cur) && cur.result !== undefined && hops < 6){
    cur.result = tryParse(cur.result);
    const r = cur.result || {};
    if (typeof r.answer === 'string' || typeof r.text === 'string' || Array.isArray(r.sources) || r.artifacts) break;
    cur = r; hops++;
  }
  return (cur && typeof cur.result === 'object') ? cur.result : cur;
}

const cur         = item.json ?? {};
const writerItems = $items('Run RAG_Router (Writer)', 0) || [];
const writerRaw   = writerItems[0]?.json || {};
const critic      = $items('Apply Critic Feedback', 0)?.[0]?.json ?? {};

const flatWriter  = flattenToPayload(writerRaw);
const flatCur     = flattenToPayload(cur);

// 1) Writer bevorzugt, dann aktuelles Item (KEIN Critic-Fallback)
const writerAnswer = pickStr(
  flatWriter?.answer, flatWriter?.text,
  get(writerRaw,'result.answer'), get(writerRaw,'result.text'),
  get(writerRaw,'choices.0.message.content'), get(writerRaw,'choices.0.text'),
  get(writerRaw,'data.choices.0.message.content')
);
const curAnswer = pickStr(
  flatCur?.answer, flatCur?.text,
  get(cur,'result.answer'), get(cur,'result.text'),
  get(cur,'choices.0.message.content'), get(cur,'choices.0.text'),
  get(cur,'data.choices.0.message.content')
);

const rawText = writerAnswer || curAnswer || '';
const { text: cleanText, embeddedNotes } = sanitizeAnswer(rawText);

// 2) Quellen & Artefakte mergen (Writer bevorzugt)
const sources =
  (Array.isArray(cur.sources) && cur.sources) ||
  (Array.isArray(flatCur?.sources) && flatCur.sources) ||
  (Array.isArray(writerRaw.sources) && writerRaw.sources) ||
  (Array.isArray(flatWriter?.sources) && flatWriter.sources) || [];

const artifacts = {
  ...(flatCur?.artifacts || {}),
  ...(cur.artifacts || {}),
  ...(flatWriter?.artifacts || {}),
  ...(writerRaw.artifacts || {}),
};

// 3) Moderator-Notizen aus State + ggf. eingebettete Notes hinzuf√ºgen
const state = cur.state || writerRaw.state || critic.state || {};
const moderatorNotes = String(state.boardModeratorNotes || '').trim();
const notesMerged = [ moderatorNotes, embeddedNotes ].filter(Boolean).join('\n\n');
if (notesMerged) artifacts.moderator_notes = notesMerged;

// 4) Setzen ‚Äî boardText NICHT √ºberschreiben (bleibt Persona-only)
cur.answer    = cleanText;
cur.sources   = cur.sources ?? sources;
cur.artifacts = artifacts;

item.json = cur;
return item;

// Agents Preflight (Normalize) ‚Äî SINGLE item with personas[]
const base = ($json && typeof $json === 'object') ? $json : {};

let personas = base.personas ?? base.body?.personas ?? [];
if (typeof personas === 'string') {
  try { personas = JSON.parse(personas); }
  catch {
    personas = personas.split(/\r?\n|;|\|/).map(s => ({ label: s.trim() })).filter(p => p.label);
  }
}
if (!Array.isArray(personas) || personas.length === 0) {
  personas = [{
    label: base.persona_label || base.label || 'Agent',
    provider: base.persona_provider || base.provider || base.model || 'groq',
    model: base.persona_model || base.openai_model || base.groq_model || base.ollama_model || base.mistral_model || base.hf_model || base.model || 'llama3',
    system: base.persona_system || base.system || '',
    prompt: base.persona_prompt || ''
  }];
}

// optional: Mapping beibehalten, aber NICHT mehr pro Item ausgeben
function mapProvider(prov, model){
  const p = String(prov || '').toLowerCase();
  if (p === 'groq')        return { model: 'groq',       groq_model: model };
  if (p === 'openai')      return { model: 'openai',     openai_model: model };
  if (p === 'anthropic')   return { model: 'anthropic',  claude_model: model };
  if (p === 'mistralapi')  return { model: 'mistralapi', mistral_model: model };
  if (p === 'openrouter')  return { model: 'openrouter', openrouter_model: model || 'openai/gpt-oss-120b' };
  if (p === 'huggingface') return { model: 'huggingface', hf_model: model };
  return { model: 'mistral',  ollama_model: model || 'llama3' };
}

personas = personas.map((p, i) => {
  const prov = p.provider || base.provider || 'groq';
  const mdl  = p.model || base.model || 'llama3';
  const mapped = mapProvider(prov, mdl);
  return { ...p, label: p.label || `Agent ${i+1}`, provider: prov, model: mdl, ...mapped };
});

// EIN Item mit personas[]
return [{ json: { ...base, personas } }];

// Build Writer Prompt ‚Äî Persona-only material, strip <think>, promote prompt/system, **carry job_id + __event_base**

const j = ($json && typeof $json === 'object') ? $json : {};
const norm = v => String(v ?? '').trim();
const isArr = a => Array.isArray(a);

// ---- Provider/Model (unver√§ndert aus deiner letzten Fassung) ----
const allowed = new Set(['groq','openai','anthropic','mistralapi','openrouter','huggingface','mistral']);
const provIn  = norm(j.writer?.provider || j.model || j.provider).toLowerCase();
const provider = allowed.has(provIn) ? provIn : 'groq';

let modelFields = {};
switch (provider) {
  case 'groq':       modelFields = { model:'groq',       groq_model: j.groq_model || j.writer?.model || 'llama-3.1-70b-versatile' }; break;
  case 'openai':     modelFields = { model:'openai',     openai_model: j.openai_model || j.writer?.model || 'gpt-4o-mini'           }; break;
  case 'anthropic':  modelFields = { model:'anthropic',  claude_model: j.claude_model || j.writer?.model || 'claude-3-5-sonnet'     }; break;
  case 'mistralapi': modelFields = { model:'mistralapi', mistral_model: j.mistral_model || j.writer?.model || 'mistral-large-latest'}; break;
  case 'openrouter': modelFields = { model:'openrouter', openrouter_model: j.openrouter_model || j.writer?.model || 'openai/gpt-oss-120b' }; break;
  case 'huggingface':modelFields = { model:'huggingface',hf_model: j.hf_model || j.writer?.model || 'meta-llama/Meta-Llama-3-8B-Instruct'}; break;
  case 'mistral':
  default:           modelFields = { model:'mistral',    ollama_model: j.ollama_model || j.writer?.model || 'llama3'                }; break;
}

// ---- Persona-only Material + Think-strip (defensiv) ----
const stripThink = s => String(s || '').replace(/<think>[\s\S]*?<\/think>\s*/g,'').trim();
const personaBoardRaw =
  j.boardPersonaText ??
  (isArr(j.board) ? j.board.map(b=>`### ${b.label||'Agent'}\n${norm(b.text)}`).join('\n\n') : '');
const boardPersonaText = stripThink(personaBoardRaw);
const basePrompt = j.prompt ?? j.body?.prompt ?? j.input?.prompt ?? '';

const writerSystem = [
  (j.system || j.body?.system || ''),
  'Du bist der **Writer/Redakteur**.',
  'Erzeuge eine klare, strukturierte Abschlussfassung ausschlie√ülich auf Basis der Persona-Inhalte.',
  'Ignoriere Moderator-/Critic-Notizen, Tool-/Observation-Texte und interne Denkabschnitte.',
  'Struktur: 1) Kurzfassung (max. 5 S√§tze)  2) Details je Bereich  3) Entscheidungen  4) Offene Punkte / Risiken',
  'Formatiere mit sinnvollen √úberschriften und Listen.'
].filter(Boolean).join('\n');

const writerPrompt = [
  `Teamaufgabe: ${basePrompt}`,
  '',
  'Team-Ergebnisse (nur Persona-Inhalte):',
  boardPersonaText || '(keine Persona-Inhalte verf√ºgbar)'
].join('\n');

// ---- üîß job_id + event-base robust √ºbernehmen ----
const state = j.state || {};
const jobId =
  j.job_id ??
  j.jobId ??
  state.job_id ??
  j.__event_base?.job_id ??
  j.__event?.job_id ??
  j.body?.job_id ??
  null;

// Runde / Runden
const round        = Number(state.round ?? j.round ?? 1);
const rounds_total = Number(state.rounds_total ?? state.agent_rounds ?? j.agent_rounds ?? 1);

// __event_base vorbereiten (vom Writer-Branch aus nutzbar)
const eventBase = {
  job_id: jobId,
  persona: 'Writer',
  role: 'writer',
  round,
  rounds_total
};

// ---- Promote prompt/system & carry job_id ----
const promoted = {
  ...modelFields,
  prompt: writerPrompt,
  system: writerSystem,
  rag: !!j.rag,
  rag_tags: isArr(j.rag_tags) ? j.rag_tags : [],
  conversation_id: j.conversation_id ?? null,
  memory: j.memory ?? null,
  job_id: jobId,                 // <<‚Äî‚Äî WICHTIG: top-level job_id f√ºr Subflows
  __event_base: eventBase        // <<‚Äî‚Äî WICHTIG: f√ºr emit_event-Subflow
};

// Keep writer_call for logging/debugging
const writer_call = { ...promoted };

return { json: { ...j, ...promoted, writer_call } };

// Init Round State
const j = $json || {};
const rounds = Number(j.agent_rounds ?? j.body?.agent_rounds ?? 1);
return [{
  json: {
    ...j,
    round: 1,
    agent_rounds: Math.max(1, rounds),
    board: Array.isArray(j.board) ? j.board : [],
    boardText: String(j.boardText || ''),
  }
}];

// Prepare Critic/Facilitator Prompt ‚Äî keine Resets von round/agent_rounds
const j = $json || {};

// Provider-Mapping (separat steuerbar √ºber j.critic)
const allowed = new Set(['groq','openai','anthropic','mistralapi','openrouter','huggingface','mistral']);
const provIn  = String(j.critic?.provider || j.model || j.provider || '').toLowerCase();
const provider = allowed.has(provIn) ? provIn : 'groq';
const mdlIn   = String(j.critic?.model || '').toLowerCase();

let mapped = {};
switch (provider) {
  case 'groq':       mapped = { model: 'groq',       groq_model: mdlIn || j.groq_model || 'llama-3.1-70b-versatile' }; break;
  case 'openai':     mapped = { model: 'openai',     openai_model: mdlIn || j.openai_model || 'gpt-4o-mini' }; break;
  case 'anthropic':  mapped = { model: 'anthropic',  claude_model: mdlIn || j.claude_model || 'claude-3-5-sonnet' }; break;
  case 'mistralapi': mapped = { model: 'mistralapi', mistral_model: mdlIn || j.mistral_model || 'mistral-large-latest' }; break;
  case 'openrouter': mapped = { model: 'openrouter', openrouter_model: mdlIn || j.openrouter_model || 'openai/gpt-oss-120b' }; break;
