function pick(...vals) {
  for (const v of vals) {
    if (v === undefined || v === null) continue;
    if (typeof v === 'string' && v.trim() === '') continue;
    return v;
  }
  return undefined;
}
function pickFromItems(keys) {
  for (const it of allItems) {
    const j = it?.json || {};
    for (const k of keys) {
