// Parse Tool Calls from LLM text ‚Äî robust, mehrere JSON-Objekte erlaubt
// Erwartet in den Agenten-Antworten eingebettete Bl√∂cke wie:
// {"tool":"web.search","input":{"query":"...", "top_k":3, "recency_days":30}}
// {"tool":"db.query","input":{"kind":"postgres","conn":"cred:pg_ro","sql":"SELECT ... LIMIT 25"}}

const MAX_CALLS = 3;

function extractJsonObjects(s) {
  const out = [];
  const text = String(s || "");
  // Simple JSON object finder (tolerant)
  const regex = /\{[\s\S]*?\}/g;
  let m;
  while ((m = regex.exec(text)) !== null) {
    try { out.push(JSON.parse(m[0])); } catch {}
  }
  return out;
}

const items = $input.all();
