  const raw = j.text || j.result || j.response || "";
  const found = extractJsonObjects(raw).filter(o => o && o.tool && o.input);
  for (const f of found) calls.push(f);
}

// Limit & normalize
const allowed = calls.slice(0, MAX_CALLS).map((c, i) => ({
  tool: String(c.tool).toLowerCase().trim(),
  input: c.input || {},
  _tool_idx: i + 1
}));

return [{
