    return `## Tool #${idx+1}: Programm-Ausgabe (Exit ${o.exitCode})\n${out}${note}`;
  }
  return `## Tool #${idx+1}: ${o.tool}\n${JSON.stringify(o).slice(0, 1000)}`;
}

