// Inputs: state.thinkPersona, state.thinkWriter
// Output: artifacts.rationale_summary = { persona: [ {round,label,bullets[]}... ], writer: [bullets[]] }

function summarize(lines) {
  // sehr simple Heuristik: kurze Stichpunkte aus den Zeilen bauen (keine Original-S√§tze √ºbernehmen)
  // -> in der Praxis kannst du hier auch einen LLM-Call mit striktem Prompt nutzen,
  //    der EXPLIZIT keine Zitate/Schrittfolgen erzeugen darf.
  const text = lines.join(' ').replace(/\s+/g,' ').slice(0, 2000);
  const words = text.split(/\W+/).filter(Boolean);
  const freqs = new Map();
  for (const w of words) {
    const k = w.toLowerCase();
    if (k.length < 4) continue;
    freqs.set(k, (freqs.get(k)||0)+1);
  }
  const top = Array.from(freqs.entries()).sort((a,b)=>b[1]-a[1]).slice(0,7).map(([w])=>w);
  const bullets = top.slice(0,5).map(w => `Hinweis zu: ${w}`);
  return bullets.length ? bullets : ['(keine verwertbaren Hinweise)'];
}

